declare const clearCache: ({ owner, repo, ref, path, }: {
    owner: string;
    repo: string;
    ref: string;
    path?: string | undefined;
}) => void;

declare const gql: ({ projectRoot, query, variables, }: {
    projectRoot: string;
    query: string;
    variables: object;
}) => Promise<ExecutionResult<{
    [key: string]: any;
}, {
    [key: string]: any;
}> | {
    error: any;
}>;
declare const buildSchema: (projectRoot: string) => Promise<GraphQLSchema>;
declare const githubRoute: ({ accessToken, owner, repo, query, variables, rootPath, branch, }: {
    accessToken: string;
    owner: string;
    repo: string;
    query: string;
    variables: object;
    rootPath?: string | undefined;
    branch: string;
}) => Promise<ExecutionResult<{
    [key: string]: any;
}, {
    [key: string]: any;
}>>;

export { buildSchema, clearCache, githubRoute, gql };
