declare const defineSchema: (config: TinaCloudSchema) => {
    _definitionType: string;
    config: TinaCloudSettings;
};
interface TinaCloudSchema {
    collections: TinaCloudSection[];
}
interface TinaCloudSettings {
    collections: TinaCloudSection[];
}
interface TinaCloudSection {
    path: string;
    name: string;
    label: string;
    templates: TinaCloudTemplate[];
}
interface TinaCloudTemplate {
    label: string;
    name: string;
    fields: TinaField[];
}
declare type TinaField = TextField | DateTimeField | NumberField | TextareaField | SelectField | GroupField | GroupListField | ListField | ToggleField | TagsField | BlocksField | Reference | ReferenceList;
interface TinaBaseField {
    name: string;
    label: string;
    description?: string;
}
interface TextField extends TinaBaseField {
    type: "text";
}
interface DateTimeField extends TinaBaseField {
    type: "datetime";
    dateFormat?: string;
    timeFormat?: string;
}
interface NumberField extends TinaBaseField {
    type: "number";
}
interface TextareaField extends TinaBaseField {
    type: "textarea";
}
interface SelectField extends TinaBaseField {
    type: "select";
    options: string[];
}
interface GroupField extends TinaBaseField {
    type: "group";
    fields: TinaField[];
}
interface GroupListField extends TinaBaseField {
    type: "group-list";
    fields: TinaField[];
}
interface ListField extends TinaBaseField {
    type: "list";
    field: {
        component: "text" | "textarea" | "number" | "select";
    };
}
interface ToggleField extends TinaBaseField {
    type: "toggle";
}
interface TagsField extends TinaBaseField {
    type: "tags";
}
interface BlocksField extends TinaBaseField {
    type: "blocks";
    templates: TinaCloudTemplate[];
}
interface Reference extends TinaBaseField {
    type: "reference";
    collection: string;
}
interface ReferenceList extends TinaBaseField {
    type: "reference-list";
    collection: string;
}

